/* c.a.s.m.SampleMapper.getSampleList */ SELECT
-- COUNT(*) OVER() AS total_count, -- ORACLE (SELECT COUNT(*) FROM sample
AND dept_cd = #{deptCd}
AND age = #{age}
AND name like '%' || #{name} || '%'
AND gender = #{gender}
AND eye_color = #{eyeColor}
AND company = #{company}
) AS total_count, -- FIXME temp code
key, dept_cd, (SELECT dept_nm FROM sample_dept WHERE dept_cd = a.dept_cd) dept_nm, index, guid, is_active, balance, picture, age, eye_color, name, gender, company, email, phone, address, about, to_char(registered, 'YYYYMMDD') registered, latitude, longitude, greeting, favorite_fruit, file_id FROM sample a
AND dept_cd = #{deptCd}
AND age = #{age}
AND name like '%' || #{name} || '%'
AND gender = #{gender}
AND eye_color = #{eyeColor}
AND company = #{company}
ORDER BY index DESC
OFFSET ${startRowNum - 1} ROWS FETCH NEXT ${countPerPage} ROWS ONLY

/* c.a.s.m.SampleMapper.getSampleBigList */
SELECT index || '-' || LPAD(ROWNUM, 6, '0') AS index, name, email, phone, age, gender, eye_color, favorite_fruit
  FROM sample, sample


   AND dept_cd = #{deptCd}


   AND age = #{age}


   AND name like '%' || #{name} || '%'


   AND gender = #{gender}


   AND eye_color = #{eyeColor}



