mapper.select:
	    /* common.file.mappers.FileMapper.getFile */
        SELECT file_name,
               file_name_ext,
               file_size,
               copy_file_path,
               copy_file_name
          FROM file
         WHERE file_id = #{fileId}
           AND file_seq = #{fileSeq}

mapper.delete:
/* common.file.mappers.FileMapper.deleteFile */
DELETE 
  FROM file
 WHERE file_id = #{fileId}
   AND file_seq = #{fileSeq}
